package com.cloud.config.server;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.cloud.config.server.EnableConfigServer;import org.springframework.cloud.netflix.eureka.EnableEurekaClient;/** * @EnableConfigServer 注解开启配置服务器的功能 * 分布式配置中心组件 * 每一个实例注册之后需要向注册中心发送心跳（因此可以在内存中完成）， * 在默认情况下erureka server也是一个eureka client ,必须要指定一个 serve * * /{application}/{profile}[/{label}] *{application}-{profile}.yml/{label}/{application}-{profile}.yml/{application}-{profile}.properties/{label}/{application}-{profile}.properties */@EnableEurekaClient@EnableConfigServer@SpringBootApplicationpublic class CloudConfigServerApplication {	public static void main(String[] args) {		SpringApplication.run(CloudConfigServerApplication.class, args);	}}